#include <bits/stdc++.h>using namespace std; void solve(){    // LOGIC    int n;    cin >> n;        vector<int> v(n + 1);     for(int i = 1; i<= n;i++) cin >> v[i];     int ans = 0;     for(int i = 1; i <= n; i++){        int cur = i;        int m = 0;        while(cur != -1){            m++;            cur = v[cur];        }        ans= max(m, ans);    }    cout << ans;} int main(){    ios::sync_with_stdio(false); // Fast I/O    cin.tie(nullptr);            // Untie cin from cout   solve();}