#include <bits/stdc++.h>using namespace std; void solve(){    // LOGIC    int n;    cin >>n;    int c = 0;    int ans = -1;    for(int i = 0; i < n;i++){        int a, b;        cin >> a >> b;        c -= a;        c += b;        ans = max(ans, c);    }     cout << ans << endl;} int main(){    ios::sync_with_stdio(false); // Fast I/O    cin.tie(nullptr);            // Untie cin from cout      solve();}