#include <bits/stdc++.h>using namespace std;#define ll long long vector<ll> a,b;ll n;vector<vector<ll>> dp;  ll rec(ll cur, ll prev){    if(cur >= a.size()) return 0;      ll ans = -1e6;    ll take;    ll f = 0, s = 0;    if(dp[cur][prev] != - 1) return dp[cur][prev];        if(prev == 1 || prev == 0){        f = b[cur] + rec(cur + 1, 2);    }    if(prev == 2 || prev == 0){        s = a[cur] + rec(cur + 1, 1);     }    ans = max(ans, rec(cur+1, 0));     ans = max({ans, f, s});     return dp[cur][prev] = ans;  }  void solve(){    // LOGIC    ll n;    cin >> n;    a.assign(n, 0);    b.assign(n, 0);    for(ll i =0 ;i < n; i++) cin >> a[i];    for(ll i =0 ;i < n; i++) cin >> b[i];    dp.assign(1e5, vector<ll>(3, -1));     ll f = rec(0, 0);    ll s = rec(1, 0);      cout << max(f,s) << endl;   } int main(){    ios::sync_with_stdio(false); // Fast I/O    cin.tie(nullptr);            // Untie cin from cout solve();}