#include <bits/stdc++.h>using namespace std; void solve(){    // LOGI    int n, p;    cin >> n >> p;    int c = 0;    for(int i = 0; i < 61; i++){        long long cur = n - (1LL * i * p);        if(cur < 0) continue;        if(cur >= i && __builtin_popcountll(cur) <= i) {cout << i << endl; return;};     }    cout << -1 << endl;} int main(){    ios::sync_with_stdio(false); // Fast I/O    cin.tie(nullptr);            // Untie cin from cout      solve();}