#include <bits/stdc++.h>using namespace std;#define ll long long void solve(){    // LOGIC    ll n;    cin >> n;    vector<int> v;     int i = 2, c = 0;    while(i * i <= n){        if(c == 2) break;         if(n % i == 0){            n /= i;            v.push_back(i);            c++;        }        i++;      }    if(v.size() != 2){ cout << "NO\n"; return;}     ll a = v[0], b = v[1];     if(n == 1 || a == n || b == n) cout << "NO\n";    else cout << "YES\n"<< a << " " << b << " " << n << endl; } int main(){    ios::sync_with_stdio(false); // Fast I/O    cin.tie(nullptr);            // Untie cin from cout     int t;    cin >> t;    while(t--) solve();}