#include <bits/stdc++.h>using namespace std; void solve(){    // LOGIC    string s;    cin >> s;    bool alcap = true, cap = true;     for(auto i  : s){        if(isupper(i)) continue;        else{            alcap = false;        }    }     if(alcap){        for(auto i: s){            cout<<(char) tolower(i);        }        cout << endl;        return;    }     if(islower(s[0])){        for(int i = 1; i < s.size(); i++){            if(isupper(s[i])) continue;            else{                cap = false;            }        }        // return;    }    else cap = false;     if(cap){        cout <<(char) toupper(s[0]);        for(int i = 1; i < s.size(); i++){            cout <<(char) tolower(s[i]);        }        cout << endl;        return;    }      cout << s << endl;} int main(){    ios::sync_with_stdio(false); // Fast I/O    cin.tie(nullptr);            // Untie cin from cout     solve();}