#include <bits/stdc++.h>using namespace std; #define ll long long void solve(){    // LOGIC    ll x, y,a,b;    cin >> x >> y >> a >> b;    ll s = min(b, 2 * a);    ll i = min(x , y);    ll sum = (i * s);    sum += (abs(x - y)) * a;    cout << sum << endl;} int main(){    ios::sync_with_stdio(false); // Fast I/O    cin.tie(nullptr);            // Untie cin from cout     int n;    cin >> n;    while(n--) solve();}