#include <bits/stdc++.h>using namespace std; #define ll long long void solve(){    // LOGIC    ll n,k;    cin >> n >> k;     ll a = k / (n - 1);    ll b = k % (n - 1);     if(b == 0){        cout << a * n - 1<< endl;    }    else cout << a * n  + b << endl;  } int main(){    ios::sync_with_stdio(false); // Fast I/O    cin.tie(nullptr);            // Untie cin from cout     int n;    cin >> n;    while(n--) solve();}