#include <bits/stdc++.h>using namespace std; void solve(){    // LOGIC    int n;    cin >> n;     vector<int> v(n);     for(int i = 0; i < n ; i++) cin >> v[i];     unordered_map<long long , long long> m;     for(auto i : v){        m[__builtin_clz(i)]++;    }    long long c = 0;     for(auto i: m){        if(i.second != 0)        c += ((i.second) * (i.second - 1) /2);    }     cout << c << endl;} int main(){    ios::sync_with_stdio(false); // Fast I/O    cin.tie(nullptr);            // Untie cin from cout     int n;    cin >> n;    while(n--) solve();}