#include <bits/stdc++.h>using namespace std; void solve(){    // LOGIC    int n;    cin >> n;    vector<int> v(n);     for(int i = 0; i < n; i++) cin >> v[i];     vector<pair<int, int>> ans;    int val1 = v[0],f = 0, val2;     for(int i = 1; i < n; i++){        if(val1 != v[i]){            ans.push_back({0 + 1, i + 1});            f = 1;            val2 = i;        }    }     if(f == 0){ cout << "NO\n"; return;}     for(int i = 1; i < n; i++){        if(val1 == v[i]){            ans.push_back({val2 + 1, i + 1});        }    }     cout << "YES\n";     for(auto i : ans){        cout << i.first << " " << i.second << endl;    } } int main(){    ios::sync_with_stdio(false); // Fast I/O    cin.tie(nullptr);            // Untie cin from cout     int n;    cin >> n;    while(n--) solve();}