#include <bits/stdc++.h>using namespace std;#define ll long long void solve(){    // LOGIC    int n, k;    cin >> n >> k;     map<ll, ll> v;    ll ans = 0;      for(int i = 0; i< n; i++){        ll a;        cin >> a;        v[a % k]++;    }    // for(auto i : v){    //     cout << i.first <<"f s" << i.second<< " ";    // }     for(int i = 1; i<  k /2 + 1 ; i++){        ll x = max(v[i], v[k - i]);        ll y = min(v[k - i], v[i]);        if(y == 0){            ans += x;            continue;        }        // cout << i<< " p" << k - i << " ";         if(x == y || x == y + 1) ans += 1;        else {            ans += 1;            ans += (x - y - 1);         }        }     if(v[0] != 0) ans++;     cout << ans << endl;  } int main(){    ios::sync_with_stdio(false); // Fast I/O    cin.tie(nullptr);            // Untie cin from cout     int n;    cin >> n;    while(n--) solve();}