#include <bits/stdc++.h>using namespace std; void solve(){    // LOGIC    int n;    cin >> n;     string a, b;    cin  >> a >> b;     int zero = 0, one = 0;     for(auto i: a) {        if(i == '0') zero++;        else one++;    }    bool flip = false;     int i = n -1;     while(i >= 0){        if(a[i] == b[i] && flip == 0){            if(a[i] == '0') zero--;            else one--;        }        else if(a[i] != b[i] && flip == 1){            if(a[i] == '0') zero--;            else one--;        }        else{            if(zero == one){                flip = !flip;                // cout<<"came here";                i++;            }            else{                 cout <<"NO\n";                return;            }        }        i--;        }     cout << "YES\n";  } int main(){    ios::sync_with_stdio(false); // Fast I/O    cin.tie(nullptr);            // Untie cin from cout     int n;    cin >> n;    while(n--) solve();}