#include <bits/stdc++.h>using namespace std; void solve(){    // LOGIC    int n;    cin >> n;     vector<int> v(n);    unordered_map<int, int> m;     for(int i = 0; i< n; i++) {        cin >> v[i];        v[i] -= i;        m[v[i]]++;    }     // for(auto i : m){    //     cout << i.first <<  " " << i.second << endl;     // }     long long c = 0;    for(auto p:m){        c += (p.second * 1LL * (p.second - 1))/2;    }    cout << c << endl;  } int main(){    ios::sync_with_stdio(false); // Fast I/O    cin.tie(nullptr);            // Untie cin from cout     int n;    cin >> n;    while(n--) solve();}