#include <bits/stdc++.h>using namespace std; void solve(){    // LOGIC    int n; cin >> n;    string s;    cin >> s;     int  zero = 0;     for(auto i : s) if(i == '0') zero++;     int one = n - zero;     if(zero == 1){        cout << "BOB\n";        return;    }     if(zero & 1){        cout << "ALICE\n";        return;    }    else{        cout<< "BOB\n";        return;    }  } int main(){    ios::sync_with_stdio(false); // Fast I/O    cin.tie(nullptr);            // Untie cin from cout     int n;    cin >> n;    while(n--) solve();}