#include <bits/stdc++.h>using namespace std; #define ll long long void solve(){    // LOGIC    ll n, l, r;    cin >> n >> l >> r;     vector<ll> v(n);     for(auto &i: v) cin >> i;     sort(v.begin(), v.end());        ll c = 0;     for(int i = 0;i < n; i++){        auto low = lower_bound(v.begin() + i + 1, v.end(), abs(l -v[i]));        auto hig = upper_bound(v.begin() + i + 1, v.end(), abs(r - v[i]));        int f = 0;        if(low == v.end()) continue;        if(hig == v.end()) f = 1;        c += (hig - low);         // int li  =low -  v.begin();        // int hi = hig - v.begin() - f;         // cout << *low << "l r" << *hig << endl;         // for(int j = li;j <=hi;j++){        //     if(li == i) continue;        //     if(l <= v[j] + v[i] <= r) c++;        // }                 }     cout << c << endl;    } int main(){    ios::sync_with_stdio(false); // Fast I/O    cin.tie(nullptr);            // Untie cin from cout     int n;    cin >> n;    while(n--) solve();}