#include <bits/stdc++.h>using namespace std; void solve(){    // LOGIC    int n;    cin >> n;            vector<int> v(n);     for(int i = 0; i< n; i++) cin >> v[i];     int c = 1;    bool first = true;     for(int i = 0;i < n; i++ ){        if(v[i] == 1 and first){            cout << n + 1 <<" ";            first = 0;        }        else{            cout << c << " ";            c++;        }    }     cout << c <<" \n";} int main(){    ios::sync_with_stdio(false); // Fast I/O    cin.tie(nullptr);            // Untie cin from cout     int n;    cin >> n;    while(n--) solve();}