#include <bits/stdc++.h>using namespace std; void solve(){    // LOGIC    int n;    cin >> n;    vector<int> v(n);    long long count = 0;    int o = 0, t = 0, th = 0, f = 0;    for(int i = 0; i < n; i++){        cin >> v[i];        if(v[i] == 4) f+=1;        else if(v[i] == 3) th+= 1;        else if(v[i] == 2) t += 1;        else o+= 1;    }    int c = f;    int min31 = min(th, o);    c += th;    o-= min31;     c += t/2;    t %= 2;     if(t> 0){o -= min(2, o);    c += 1;}    if(o > 0)c += (o + 3) / 4;    cout << c<< endl; } int main(){    ios::sync_with_stdio(false); // Fast I/O    cin.tie(nullptr);            // Untie cin from cout   solve();}