#include <bits/stdc++.h>using namespace std; void solve(){    // LOGIC    int n;    cin >> n;    vector<int> v(n);    long long s = 0;    for(int i = 0; i< n; i++) {cin >> v[i]; s+= v[i];}    long long avg = s;    int ans = 0;    s = 0;    // cout << "a" << avg << endl;    sort(v.rbegin(), v.rend());    for(int i = 0; i < n; i++){        if(s > avg) break;        s += v[i];        avg -= v[i];        ans++;    }    cout << ans << endl;} int main(){    ios::sync_with_stdio(false); // Fast I/O    cin.tie(nullptr);            // Untie cin from cout     solve();}