#include <bits/stdc++.h>using namespace std;#define ll long long ll calx(ll x){    return 1ll * x * (x + 1) / 2;}   void solve(){    // LOGIC    ll n,x;    cin >> n >> x;    ll l = 1, h = 2 * n - 1, m, ans = 2 * n -1 ;     while(l <= h){        ll mid = (l + h)/2;        ll cnt = 0;        if(mid >= n){            cnt = calx(n) + calx(n - 1) - calx(2 * n - 1 - mid);        }        else cnt = calx(mid);         if(cnt >= x){            ans = mid;            h = mid - 1;        }        else l = mid + 1;    }        cout << ans << endl;     } int main(){    ios::sync_with_stdio(false); // Fast I/O    cin.tie(nullptr);            // Untie cin from cout     int n;    cin >> n;    while(n--) solve();}