#include <bits/stdc++.h>using namespace std; void solve(){    // LOGIC    int n, q;    cin >> n >> q;     vector<int> v(n);     for(int i = 0; i < n; i++) cin >> v[i];     sort(v.rbegin(), v.rend());     vector<int> pref(n);    pref[0] = v[0];     for(int i = 1; i < n; i++) {        pref[i] = pref[i - 1] + v[i];    }     // for(auto i : pref) cout << i << "a ";    // cout << endl;     for(int i = 0; i < q; i++){        int val; cin >> val;         auto it = lower_bound(pref.begin(), pref.end(), val );        if(it == pref.end()){            cout << -1 << endl;            continue;        }         cout << ( it - pref.begin() + 1) << endl;     }     } int main(){    ios::sync_with_stdio(false); // Fast I/O    cin.tie(nullptr);            // Untie cin from cout     int n;    cin >> n;    while(n--) solve();}