#include <bits/stdc++.h>using namespace std;#define ll long long int main(){    ios::sync_with_stdio(false); // Fast I/O    cin.tie(nullptr);            // Untie cin from cout     ll n, m;    cin >> n >> m;    vector<pair<int,int>> v(n, {0, 0});    ll sum = 0;    for(int i = 0; i < n; i++){        cin >> v[i].first;        sum += v[i].first;    }    pair<int, int> global = {0, - 1};    int i = 0;    while(i < m){        int a, b;        ll num;        cin >> a ;        if(a == 1){            cin >> b >> num;            if(global.second > v[b - 1].second){                sum -= global.first;                // sum += num;            }            else{                sum -= v[b - 1].first;            }            v[b-1] = {num, i + 1};            sum += num;        }        else{            cin >> num;            sum = num * n;            global = {num, i + 1};        }        i++;        cout << sum << endl;}}