#include <bits/stdc++.h>using namespace std; void solve(){    // LOGIC    int n;    cin >> n;    vector<int> v(n);     for(int i = 0; i< n; i++) cin >> v[i];     unordered_map<int, int> m;    for(auto i: v) m[i]++;     set<int> s;     for(auto i:v) s.insert(i);     int unq = s.size();     if(((n - unq) & 1) == 0 ) cout << unq << endl;    else cout << unq - 1 << endl;   } int main(){    ios::sync_with_stdio(false); // Fast I/O    cin.tie(nullptr);            // Untie cin from cout     int n;    cin >> n;    while(n--) solve();}