#include <bits/stdc++.h>using namespace std; void solve(){    // LOGIC    int n,x;    cin >> n >> x;     vector<int> v(n);    int o = 0;     for(int i = 0;i < n; i++){        cin >> v[i];        o++;        }     if(o < x){        cout << "-1\n";        return;    }     map<int, int> m;    m[0] = -1;    int s = 0;    int len = -1;     for(int i = 0; i < n; i++){        s += v[i];         if(m.count(s - x)){            len = max(len, i - m[s - x]);        }        if(!m.count(s)) m[s] = i;    }      cout << (len != -1 ? n - len : - 1) << endl;} int main(){    ios::sync_with_stdio(false); // Fast I/O    cin.tie(nullptr);            // Untie cin from cout     int n;    cin >> n;    while(n--) solve();}