#include <bits/stdc++.h>using namespace std; void solve(){    // LOGIC    int n;    cin >> n;    if(n == 1){ cout << 1 << endl;return;};    vector<int> v;    cout << n << " ";    for(int i = 1; i < n ;i++){        cout << i << " ";    }    cout << endl;    } int main(){    ios::sync_with_stdio(false); // Fast I/O    cin.tie(nullptr);            // Untie cin from cout     int n;    cin >> n;    while(n--) solve();}