#include <bits/stdc++.h>using namespace std; void solve(){    int n;    cin >> n;    string s;    cin >> s;    int i = n - 1;    string ans = "";     while(i >= 0){        if(s[i] != '0'){            int num = s[i] - '0';            char t = 'a' + ((int) s[i] - '0' )- 1;            ans += t;            i--;        }        else{            int num = stoi(s.substr(i - 2, 2));            char t = 'a' + num - 1;            ans += t;            i -= 3;        }    }     reverse(ans.begin(), ans.end());    cout << ans << endl;} int main(){    ios::sync_with_stdio(false); // Fast I/O    cin.tie(nullptr);            // Untie cin from cout     int n;    cin >> n;    while(n--) solve();}