#include <bits/stdc++.h>using namespace std; void solve(){    // LOGIC    int n; cin >> n;    vector<int> a(n), b(n), pos, neg, zero;      for(auto &i: a) cin >> i;    for(auto &i: b) cin >> i;      multiset<int> ms;        for(int i = 0;i <n;i++){        ms.insert(b[i] - a[i]);    }    int c = 0;     while(ms.size() >= 2){        int it = *ms.begin();        ms.erase(ms.begin());         auto del = ms.lower_bound(-it);        if(del == ms.end()) continue;        c++;        ms.erase(del);    }      cout << c << endl;   } int main(){    ios::sync_with_stdio(false); // Fast I/O    cin.tie(nullptr);            // Untie cin from cout     int n;    cin >> n;    while(n--) solve();}