#include <bits/stdc++.h>using namespace std;#define int long long void solve(){    // LOGIC    int n, ans = 0; cin >> n;    string s; cin >> s;     // cout << "T";    vector<bool> vis(n , false);    for(int i = 0;i < n; i++){                for(int j = i; j < n ; j+= i + 1){            if(s[j] == '1') break;             if(vis[j] == true) continue;            else{            ans += i + 1;            vis[j] = true;            }        }         // cout << "t";        }     cout << ans << endl;} signed main(){    ios::sync_with_stdio(false); // Fast I/O    cin.tie(nullptr);            // Untie cin from cout     int n;    cin >> n;    while(n--) solve();}