#include <bits/stdc++.h>using namespace std;#define ll long long void solve(){    // LOGIC    ll n;    cin >> n;    vector<ll> v(n);     for(ll i = 0;i <n; i++) cin >> v[i];    bool zero = false;     ll nig = 0, s = 0;    for(ll i = 0;i <n ; i++){        if(v[i] < 0) nig++;        if(v[i] == 0) zero = 1;        v[i] = abs(v[i]);        s += v[i];    }     sort(v.begin(), v.end());       if(zero || nig%2 == 0){        cout << s << endl;        return;    }      s = 0;    s += -v[0];     for(ll i = 1;i < n; i++){        s+= v[i];    }     cout << s << endl;  } int main(){    ios::sync_with_stdio(false); // Fast I/O    cin.tie(nullptr);            // Untie cin from cout     ll n;    cin >> n;    while(n--) solve();}