#include <bits/stdc++.h>using namespace std; void solve(){    // LOGIC    int a,b,k,d;    cin>> a >> b >> k >> d;    int c = a;    if(a == 0){        cout << "1\n";        return;    }     int s = min(b,k);    c += s + s;     b -= s ;    k -= s ;     c += min(a + 1, abs(b - k) + d);    cout << c << endl;   } int main(){    ios::sync_with_stdio(false); // Fast I/O    cin.tie(nullptr);            // Untie cin from cout     int n;    cin >> n;    while(n--) solve();}