#include <bits/stdc++.h>using namespace std; void solve(){    // LOGIC    int n; cin >> n;     string s; cin>> s;     int c = n - 1;     for(int i = 1;i + 1< n; i++){        if(s[i - 1] == s[i + 1]) c--;    }     cout << c << endl; } int main(){    ios::sync_with_stdio(false); // Fast I/O    cin.tie(nullptr);            // Untie cin from cout     int n;    cin >> n;    while(n--) solve();}