#include <bits/stdc++.h>using namespace std; void solve(){    // LOGIC    int n;    cin >>n;     if(n == 1){        cout << "1\n";        return;    }     if(n %2 ){        cout << "-1\n";    }    else{        for(int i = 0;i  < n ; i++){            if(i % 2){                cout << i << " ";            }            else{                cout << n - i << " ";            }        }        cout << endl;    } } int main(){    ios::sync_with_stdio(false); // Fast I/O    cin.tie(nullptr);            // Untie cin from cout     int n;    cin >> n;    while(n--) solve();}