#include <bits/stdc++.h>using namespace std; void solve(){    // LOGIC    int n;    cin >> n;     vector<int> v(n);     for(int i = 0; i < n; i++) cin >> v[i];     if(v.size() == 1) {        cout << "1\n";        return;    }     vector<int> ans;    ans.push_back(v[0]);    ans.push_back(v[1]);     for(int i = 2; i < n; i++){        int s = ans.size();         int x = ans[s - 2] - ans[s - 1];        int y = ans[s - 1] - v[i];         if(x > 0){            if(y > 0)  ans[s - 1] = v[i];            else if(y < 0) ans.push_back(v[i]);        }        else{            if( y < 0) ans[s- 1] = v[i];            else if(y > 0) ans.push_back(v[i]);        }    }    int c = ans.size();    if(ans[0] ==ans[1]) c--;     cout << c << endl;} int main(){    ios::sync_with_stdio(false); // Fast I/O    cin.tie(nullptr);            // Untie cin from cout     int n;    cin >> n;    while(n--) solve();}