#include <bits/stdc++.h>using namespace std; #define ll long long  void solve(){    // LOGIC    ll n , c;    cin >> n >> c;     vector<ll> v(n);     for(int i = 0; i < n; i++) cin >> v[i];     ll l = 1, r = 1e9, ans = -1;     while(l <= r){        ll m = l + (r - l)/2;        ll sum = 0;        for(int i = 0; i < n; i++){            sum += (v[i] + 2 * m) * (v[i] + 2 * m);            if(sum > c) break;        }        if(sum <= c){            ans = m;            l = m + 1;        }        else{            r = m -1;        }     }    cout << ans << endl;  } int main(){    ios::sync_with_stdio(false); // Fast I/O    cin.tie(nullptr);            // Untie cin from cout     int t;    cin >> t;    while(t--) solve();}