#include <bits/stdc++.h>using namespace std; void solve(){    // LOGIC    int n; cin >> n;    vector<int> v(n);     for(auto &i: v) cin >> i;     // sort(v.begin(), v.end());     map<int,int> m;        for(auto i : v){        m[i]++;    }    int c = 0;     vector<int> trap(n + 1, 0);     for(auto i : m){        if(i.first <= n){            for(int j = i.first ; j <= n; j+= i.first){                trap[j] += i.second;        }    }   }     cout << *max_element(trap.begin(), trap.end() ) << endl;     } int main(){    ios::sync_with_stdio(false); // Fast I/O    cin.tie(nullptr);            // Untie cin from cout     int n;    cin >> n;    while(n--) solve();}