#include <bits/stdc++.h>using namespace std;#define ll long long void solve(){    // LOGIC    int n;    cin >> n;    ll c = 0;     vector<int> a(n),b(n), ans;    for(int i = 0; i < n; i++){        cin >> a[i];    }    for(int i = 0; i < n; i++){        cin >> b[i];    }     ll m = INT_MIN;      for(int i = 0; i < n; i++){        m = max(m, a[i] - b[i] * 1LL);    }     for(int i = 0; i < n; i++){        if(m == a[i] - b[i] * 1LL){ c++;        ans.push_back(i + 1);    }    }         cout<< c << endl;     for(auto i : ans) cout << i << " ";    cout << endl;} int main(){    ios::sync_with_stdio(false); // Fast I/O    cin.tie(nullptr);            // Untie cin from cout     int n;    cin >> n;    while(n--) solve();}