#include <bits/stdc++.h>using namespace std; vector<int> v, memo; int dp(int n){    if(n == 0) return 0;    if(n < 0) return INT_MIN;     if(memo[n] != -1) return memo[n ];     int ans = INT_MIN;     for(auto i : v){        ans = max(ans, 1 + dp(n - i));        memo[n] = ans;    }    return memo[n];} void solve(){    // LOGIC    int n, a , b, c;    cin >> n >> a >> b >> c;    v.push_back(a);    v.push_back(b);    v.push_back(c);     memo.assign(n + 1, -1);     cout << dp(n) << endl;} int main(){    ios::sync_with_stdio(false); // Fast I/O    cin.tie(nullptr);            // Untie cin from cout     solve();}