#include <bits/stdc++.h>using namespace std; void solve(){    int n;    cin >> n;    vector<long long> v(n);     for(int i = 0; i < n; i++) cin >> v[i];     // alternate signs    for(int i = 0; i < n; i++){        if(i & 1) v[i] = -v[i];    }     vector<long long> pref(n + 1, 0);    map<long long, int> m;     for(int i = 0; i < n; i++){        pref[i + 1] = pref[i] + v[i];    }     for(auto x : pref){        m[x]++;        if(m[x] > 1){            cout << "YES\n";            return;        }    }    cout << "NO\n";} int main(){    ios::sync_with_stdio(false);    cin.tie(nullptr);     int t;    cin >> t;    while(t--) solve();}