#include <bits/stdc++.h>using namespace std; void solve(){    // LOGIC    int n, m;    cin >> n >> m;    vector<int> a(n), b(m);     for(int i = 0; i<n; i++){        cin >> a[i];    }    for(int i = 0; i < m; i++){        cin >> b[i];    }     sort(a.begin(), a.end());    sort(b.rbegin(), b.rend());     vector<int> c(n);    long long s = 0;    long long ans = 0;     for(int i = 0; i < n; i++){        c[i] = b[m - n + i];        s += abs(c[i] - a[i]);    }        for(int i = 0; i < n; i++){        ans = max(ans, s);        // if(i < n){        s -= abs(c[i] - a[i]);        c[i] = b[i];        s += abs(c[i] - a[i]);    }    cout << max(ans, s) << endl;    // for(auto i:c){    //     cout << i << " ";    // }    // cout << endl;} int main(){    ios::sync_with_stdio(false); // Fast I/O    cin.tie(nullptr);            // Untie cin from cout     int n;    cin >> n;    while(n--) solve();}