#include <bits/stdc++.h>using namespace std; void solve(){    // LOGIC    int n;    cin >> n;    vector<int> v(n);    vector<int> freq(26, 0);    string s = "";    for(int i = 0; i < n; i++){         cin >> v[i];        for(int j = 0; j< 26; j++){            if(v[i] == freq[j]){                s.push_back('a' + j);                freq[j]++;                break;            }        }    }    cout << s << endl ;      } int main(){    ios::sync_with_stdio(false); // Fast I/O    cin.tie(nullptr);            // Untie cin from cout     int n;    cin >> n;    while(n--) solve();}