#include <bits/stdc++.h>using namespace std; #define ll long long vector<ll> dp;vector<int> c = {1, 3, 6, 10, 15}; ll rec(ll t){    if(t == 0) return 0;    if(t < 0) return LLONG_MAX / 2; // avoid overflow     if(dp[t] != -1) return dp[t];     ll cur = LLONG_MAX / 2;    for(auto i : c){        cur = min(cur, 1 + rec(t - i));    }     return dp[t] = cur;}  int give(int n){    int ans = 0;    ans = n/15;    n %= 15;    ans += n /6;    n %= 6;    ans += n / 3;    n %= 3;    ans += n;    return ans;} void solve(){    ll t;    cin >> t;    ll ans = 1e9;    // dp.assign(t + 1, 1e9);    // dp[0] = 0;     // for(int i = 1;i <= t; i ++){    //     for(auto j : c){    //         if(i - j >= 0){    //             dp[i] = min(dp[i], 1 + dp[i - j]);    //         }    //     }    // }     if(t < 10){        ans = give(t);    }    else if( t < 20){        ans = min(give(t), 1 + give(t - 10));     }    else{        ans = min({give(t), 1 + give(t - 10), 2 + give(t - 20)});    }     cout << ans << endl;  } int main(){    ios::sync_with_stdio(false);    cin.tie(nullptr);     int n;    cin >> n;    while(n--) solve();}