#include <bits/stdc++.h>using namespace std; void solve(){    // LOGIC    int n;    cin >> n;    string s;    cin >> s;    int c = 0;    for(int i = 0; i< n - 2; i++){        string a = s.substr(i, 3);        if(a == "map" || a == "pie" || a == "mapie"){            if(a == "mapie"){                i += 4;            }            else{                i += 2;            }            c++;    }}    cout << c << endl;}int main(){    ios::sync_with_stdio(false); // Fast I/O    cin.tie(nullptr);            // Untie cin from cout     int n;    cin >> n;    while(n--) solve();}