#include <bits/stdc++.h>using namespace std; int f(int a, int b){    if(a > b) return 1;    else if (a == b) return 0;    else return -1;} void solve(){    // LOGIC    int a, b, c,d;    cin >> a >> b >> c>> d;    int ans = 0;     if(f(a,c) + f(b,d) >= 1) ans+=2;    if(f(a,d) + f(b,c) >= 1) ans+=2;    cout << ans << endl;    } int main(){    ios::sync_with_stdio(false); // Fast I/O    cin.tie(nullptr);            // Untie cin from cout     int n;    cin >> n;    while(n--) solve();}