#include <bits/stdc++.h>using namespace std; void solve(){    // LOGIC    int n;    cin >> n;    int l = INT_MIN;    string ans = "";     vector<pair<string, int>> m;    for(int i = 0; i< n ; i++){        string name;        int c;        cin >> name >> c;        m.push_back({name, c});    }    unordered_map<string , int> total;    unordered_map<string , int> cur;     for(auto i : m){        total[i.first] += i.second;     }     // for(auto i : m){    //     cout << i.first << " " << i.second << endl;    // }     int mx = INT_MIN;    for(auto i : total){        mx = max(mx, i.second);    }     for(auto i : m){        cur[i.first] += i.second;        if(total[i.first] == mx && cur[i.first] >= mx){            cout << i.first << endl;            break;        }    }     // cout << ans;} int main(){    ios::sync_with_stdio(false); // Fast I/O    cin.tie(nullptr);            // Untie cin from cout  solve();}