#include <bits/stdc++.h>using namespace std; void solve(){    // LOGIC    int n;    cin >> n;     int ans = 0;     for(int i = 0;i <n; i++){        int x;        cin >> x;         ans = max(ans, x + ((i + 1) % 2 ? (n + 1)/2 : n/2));    }     int t;       cout << ans << endl;} int main(){    ios::sync_with_stdio(false); // Fast I/O    cin.tie(nullptr);            // Untie cin from cout     int n;    cin >> n;    while(n--) solve();}