#include <bits/stdc++.h>using namespace std; void solve(){    // LOGIC    int n;    cin >> n;    vector<int> v(n);    for(int i =0; i < n; i++) cin >> v[i];     int small = v[0], big = v[0];    for(int i = 0; i< n;i++){        small = min(small, v[i]);        big = max(big, v[i]);    }    cout << (big - small) * (n - 1) << endl;} int main(){    ios::sync_with_stdio(false); // Fast I/O    cin.tie(nullptr);            // Untie cin from cout     int n;    cin >> n;    while(n--) solve();}