#include <bits/stdc++.h>using namespace std; void solve(){    // LOGIC    int n;    cin >> n;     vector<vector<int>> v(n ,vector<int>(n, 0));     for(int i = 0;i < n; i++){        for(int j = 0; j < n; j++){            cin >> v[i][j];        }    }    int ans = 0;         for (int d = -(n-1); d <= n-1; d++) {    // d = i - j    int cur = INT_MAX;    for (int i = 0; i < n; i++) {        int j = i - d;        if (j >= 0 && j < n) {            cur = min(cur, v[i][j]);            // cout << v[i][j] << " ";        }     }    if(cur < 0) ans += cur;}cout << ans * -1<<"\n"; }  int main(){    ios::sync_with_stdio(false); // Fast I/O    cin.tie(nullptr);            // Untie cin from cout     int n;    cin >> n;    while(n--) solve();}