#include <bits/stdc++.h>using namespace std; void solve(){    // LOGIC    int n ;     cin >> n;     vector<int> v(n);    for(int i = 0; i< n; i++){        cin >> v[i];    }     unordered_map<int, int> m;     for(auto i: v) m[i]++;     int uniq = 0;    int nuniq = 0;     for(auto &p : m){    if(p.second == 1) uniq++;    else nuniq++;    }     cout << (ceil(uniq/2.0 ) * 2 ) +(nuniq) << endl;  } int main(){    ios::sync_with_stdio(false); // Fast I/O    cin.tie(nullptr);            // Untie cin from cout     int n;    cin >> n;    while(n--) solve();}