#include <bits/stdc++.h>using namespace std; void solve(){    // LOGIC    int n;    cin >> n;    if((n&1) == 0){        cout << -1 << endl;    }    else{        for(int i = n; i > 0; i--){            cout << i << " ";        }        cout << endl;    }} int main(){    ios::sync_with_stdio(false); // Fast I/O    cin.tie(nullptr);            // Untie cin from cout     int n;    cin >> n;    while(n--) solve();}