#include <bits/stdc++.h>using namespace std; void solve(){    // LOGIC    int n;    cin >> n;    string a,b;    cin >> a >> b;     int p1 = 0, p2 = 0;    for(int i = 0; i < n; i++){        if(i&1){            p1 += a[i] == '1';            p2 += b[i] == '1';        }        else{            p1 += b[i] == '1';            p2 += a[i] == '1';        }    }     if(p1  <= (n + 1)/2 && p2 <= n  /2) cout << "yes\n";    else cout << "no\n";} int main(){    ios::sync_with_stdio(false); // Fast I/O    cin.tie(nullptr);            // Untie cin from cout     int n;    cin >> n;    while(n--) solve();}