#include <bits/stdc++.h>using namespace std; bool isprime(long long a) {    if (a <= 1) return false;    if (a <= 3) return true;    if (a % 2 == 0 || a % 3 == 0) return false;     // Check divisors up to sqrt(a)    for (long long i = 5; i * i <= a; i += 6) {        if (a % i == 0 || a % (i + 2) == 0) return false;    }    return true;} void solve() {    long long a, b;    cin >> a >> b;    if (b == 1) {        if (isprime(a)) {            cout << "yes\n";            return;        }    }    else if (b == 2 && a == 1){        cout << "yes\n";        return;    }    cout << "no\n";} int main() {    ios::sync_with_stdio(false);    cin.tie(nullptr);     int n;    cin >> n;    while (n--) solve();}